package org.click.lib.des;

public class Swap {
    private static String key = "test";

    private static String swap(String str) {
            String res = "";
            StringBuilder sb = new StringBuilder(str);

            int temp = 0;

            for (int i = 0; i < str.length(); i++) {
                    if (i % 7 == 0) {
                            temp = sb.charAt(i);
                            sb.setCharAt(i, (char) (temp + 5));
                    }
            }

            res = sb.toString();

            return res;
    }

    private static String reswap(String str) {
            String res = "";
            StringBuilder sb = new StringBuilder(str);

            int temp = 0;

            for (int i = 0; i < str.length(); i++) {
                    if (i % 7 == 0) {
                            temp = sb.charAt(i);
                            sb.setCharAt(i, (char) (temp - 5));
                    }
            }

            res = sb.toString();

            return res;
    }

    private static String encode(String str) {
            String encode = "";

            try {
                    encode = DesUtil.encrypt(str, swap(key));
            } catch (Exception e) {

            }
            encode = encode.replaceAll("\\s+", "");

            return encode;
    }

    private static String decode(String str) {
            String decode = "";

            try {
                    decode = DesUtil.decrypt(str, swap(key));
            } catch (Exception e) {

            }
            return decode;
    }
    
    public static void main(String[] args)
    {
    	Swap sw=new Swap();
    	String encode="";
    	
    	String decode="";
    	
    	decode=sw.decode(encode);
    	
    	System.err.println("decode:"+decode);
    	
    }
}
